// Extraer mensaje y llamar al AI
const body = $input.first().json.body || $input.first().json || {};
const message = body.message || body.query || body.text || '';

if (!message) {
  return { json: { error: 'No message provided' } };
}

// Llamar al servicio MCP
const response = await fetch('http://parking-b2b:3001/mcp/chat', {
  method: 'POST',
  headers: { 'Content-Type': 'application/json' },
  body: JSON.stringify({ message: message })
});

const data = await response.json();

return { json: data };
