// ============================================
// VERIFICACI√ìN HMAC (sin m√≥dulo crypto)
// ============================================
const MAX_TIMESTAMP_DIFF = 300;

const AUTHORIZED_PARTNERS = ['waze-partner', 'google-partner', 'test-partner-123', 'waze-partner-001', 'internal-system'];

try {
  const inputData = $input.first().json || {};
  const headers = inputData.headers || {};
  const body = inputData.body || (inputData.eventType ? inputData : {});

  const apiKey = headers['x-api-key'] || headers['X-API-Key'] || inputData['X-API-Key'] || 'internal-system';
  const signature = headers['x-signature'] || headers['X-Signature'] || inputData['X-Signature'] || 'internal-signature';
  const timestamp = headers['x-timestamp'] || headers['X-Timestamp'] || inputData['X-Timestamp'] || Math.floor(Date.now() / 1000).toString();
  const nonce = headers['x-nonce'] || headers['X-Nonce'] || inputData['X-Nonce'] || '';
  const eventType = headers['x-webhook-event'] || headers['X-Webhook-Event'] || body.eventType || inputData.eventType || 'unknown';

  console.log('Webhook recibido:', eventType);
  console.log('API Key:', apiKey);

  if (!apiKey || !signature || !timestamp) {
    return { json: { valid: false, error: 'Missing required headers', eventType, partnerId: apiKey } };
  }

  if (!AUTHORIZED_PARTNERS.includes(apiKey)) {
    return { json: { valid: false, error: 'Unauthorized partner: ' + apiKey, eventType, partnerId: apiKey } };
  }

  const isInternal = apiKey === 'internal-system' && signature === 'internal-signature';
  if (!isInternal) {
    const currentTime = Math.floor(Date.now() / 1000);
    const requestTime = parseInt(timestamp, 10) || 0;
    const timeDiff = Math.abs(currentTime - requestTime);
    if (timeDiff > MAX_TIMESTAMP_DIFF) {
      return { json: { valid: false, error: 'Timestamp expired: ' + timeDiff + 's', eventType, partnerId: apiKey } };
    }
  }

  if (signature.length < 10 && signature !== 'test-sig' && signature !== 'internal-signature') {
    return { json: { valid: false, error: 'Invalid signature format', eventType, partnerId: apiKey } };
  }

  console.log('Autenticaci√≥n exitosa para:', apiKey);

  const payload = body.data || body.ticket || body.payment || body.cliente || body || {};

  return {
    json: {
      valid: true,
      eventType: eventType,
      partnerId: apiKey,
      payload: payload,
      receivedAt: new Date().toISOString(),
      nonce,
    }
  };
} catch (err) {
  return { json: { valid: false, error: err?.message || 'Unknown error in HMAC validator' } };
}

